    def delete(self):
        sno1 = self.lineEdit.text()
        conn = pymysql.connect(host="localhost", user="root", password="987433",database="keshe01",charset="utf8")
        sql1 = ("delete from student where student.sno='%s'") % (sno1)
        sql2 = ("delete from choose where choose.sno='%s'") % (sno1)
        sql5=("delete from belong where belong.sno='%s'")%(sno1)
        sql3=("set foreign_key_checks = 0")
