        sql6=("select belong.banjibianhao from belong where belong.sno = '%s'") % (sno1)
        sql7 = ("select score from choose,course where choose.sno = '%s' and choose.cno = course.cno") % (sno1)
        cursor1 = conn.cursor()  # 得到一个可执行的游标对象
        cursor2 = conn.cursor()
        cursor3 = conn.cursor()
        cursor4 = conn.cursor()
